INSERT INTO `bosscms_theme` (`id`, `extent`, `parent`, `core`, `name`, `value`, `lang`) VALUE ('9', '0', '0', 'introduce', 'content', '<h2>本站 由BOSSCMS建站系统搭建</h2><p><br/></p><p><span style=\"font-size: 16px;\">BOSSCMS是一款基于自主研发PHP框架+MySQL架构的内容管理系统，能够满足各类网站开发建设的需求。系统开源、安全、稳定、简洁、易开发、专注为中小型企业及政企单位、个人站长、广大开发者、建站公司提供一套简单好用的网站内容管理系统解决方案。官网提供了大量的网站模板，可以让您不需要任何的专业技术就能轻松搭建您的网站，易操作、易上手、快捷方便的后台操作让您10分钟就会建立自己的网站。</span></p><p><br/></p><p><span style=\"font-size: 16px;\">BOSSCMS操作简单、功能完善、安全稳定、支持多语言、支持可视化编辑、文件存储(本地或外部存储)、自定义页面URL、内置SEO搜索引擎优化机制、广告违禁词、城市分站等功能。让新手能够快速搭建一个符合流量站的网站。</span></p>', '1') ;
